INSERT INTO `db_mode` VALUES (272, 'h_select', 'P_other', 'high', 0, 0, '', '/*自定义选择框*/\r\n.HSelect ol{ width:100px; height:30px; line-height:30px; position:relative; float:left;}\r\n.HSelect ol h6{ width:70px; height:30px; text-indent:10px; background-color:#eee; font-size:12px;}\r\n.HSelect ol i{ float:right; width:30px; height:30px; text-align:center; background-color:#ddd; cursor:pointer;}\r\n.HSelect ol ul{ display:none; position:absolute; top:29px; left:0; width:70px;}\r\n.HSelect ol ul li{ height:30px; text-indent:10px; background-color:#eee; cursor:pointer;}', '<!--自定义选择框 start-->\r\n<div class="HSelect">\r\n	<ol class="h_clk">\r\n		<input type="hidden" name="prov" value="" />\r\n		<h6 class="fl">省份</h6>\r\n		<i class="h_a">></i>\r\n		<ul class="h_div">\r\n			<li>北京</li>\r\n			<li>广东</li>\r\n		</ul>\r\n	</ol><!---->\r\n	<ol class="h_clk">\r\n		<input type="hidden" name="city" value="" />\r\n		<h6 class="fl">城市</h6>\r\n		<i class="h_a">></i>\r\n		<ul class="h_div">\r\n			<li>广州</li>\r\n			<li>佛山</li>\r\n		</ul>\r\n	</ol><!---->\r\n	<ol class="h_clk">\r\n		<input type="hidden" name="town" value="" />\r\n		<h6 class="fl">城镇</h6>\r\n		<i class="h_a">></i>\r\n		<ul class="h_div">\r\n			<li>天河</li>\r\n			<li>越秀</li>\r\n		</ul>\r\n	</ol><!---->\r\n</div><!--HSelect end-->\r\n<script>\r\n$(document).ready(function(){\r\n	//自定义选择框\r\n	$(".HSelect ul li").click(function(){\r\n		var optOn=$(this).text();\r\n		$(this).parent().siblings("input").val(optOn);\r\n		$(this).parent().siblings("h6").text(optOn);\r\n		$(this).addClass("none").siblings("li").removeClass("none");\r\n	});\r\n});//--ready end--\r\n</script>\r\n<!--自定义选择框 end-->', '', '', '', '', '同页多个选择框', '2014-12-28 15:53:09', '14-11-22 09:14:16');
