INSERT INTO `db_mode` VALUES (279, 'h_panelslider', 'P_other', 'plus', 0, 0, '', 'body { font-family: Arial; background: #eee; }\r\nh1, h2 { margin-top: 0; }\r\np { color: #ccc; line-height: 1.5; }\r\n#page { width: 940px; height: 400px; padding: 20px; margin: 0 auto; background: white; }\r\n\r\n.panel {\r\n  display: none;\r\n  width: 220px;\r\n  padding: 20px;\r\n  background-color: #333;\r\n  color: #fff;\r\n  box-shadow: inset 0 0 5px 5px #222;\r\n}', '<!--侧面板滑块 start-->\r\n  <!-- Centered page -->\r\n  <div id=\\"page\\">\r\n    <h1>Panel slider example</h1>\r\n    <a id=\\"left-panel-link\\" href=\\"#left-panel\\">打开左面板</a> |\r\n    <a id=\\"right-panel-link\\" href=\\"#right-panel\\">打开右面板</a>\r\n    <hr />\r\n    \r\n  </div>\r\n\r\n  <!-- Left panel -->\r\n  <div id=\\"left-panel\\" class=\\"panel\\">\r\n    <h2>Left panel</h2>\r\n    <p>Click anywhere outside panel to close it.</p>\r\n  </div>\r\n\r\n  <!-- Right panel -->\r\n  <div id=\\"right-panel\\" class=\\"panel\\">\r\n    <h2>Right panel</h2>\r\n    <p>Clicking outside this panel wont close it because <code>clickClose = false</code></p>\r\n    <button id=\\"close-panel-bt\\">Close</button>\r\n  </div>\r\n<script src=\\"plus/jquery.panelslider.min.js\\"></script>\r\n<script>\r\n//侧面板滑块\r\n$(\\''#left-panel-link\\'').panelslider();\r\n$(\\''#right-panel-link\\'').panelslider({side: \\''right\\'', clickClose: false, duration: 200 });\r\n\r\n$(\\''#close-panel-bt\\'').click(function() {\r\n  $.panelslider.close();\r\n});\r\n</script>\r\n<!--侧面板滑块 end-->', '', '', '', '', '侧面板滑块', '2014-10-12 11:18:56', '14-11-22 09:13:44');
INSERT INTO `db_mode` VALUES (280, 'h_navScroll', 'P_other', 'high', 0, 0, '', '.fixed{ position:fixed; top:0; left:0; width:100%;}', '<div style="height:100px;"></div>\r\n<div class="h_nav" style="width:100%; height:30px; background-color:#FF0000;">aaa</div>\r\n<div style="height:2000px"></div>\r\n<script>\r\n$(function () {\r\n    $(window).scroll(function () {\r\n        var __scrol = $(document).scrollTop();\r\n        var __sY = 100; //开始定位的滚动高度\r\n        if (__scrol >= __sY) {\r\n            //定位导航条\r\n            if (!$(".h_nav").hasClass(''fixed'')) {\r\n                $(".h_nav").addClass(''fixed'');\r\n            }\r\n        } else {\r\n            $(".h_nav").removeClass(''fixed'');\r\n        }\r\n    })\r\n})\r\n</script>', '', '', '', '', '导航滚动后定位', '2014-11-21 23:21:15', '14-11-22 09:13:26');
INSERT INTO `db_mode` VALUES (281, 'h_marquee', 'P_other', 'high', 0, 0, '', '', '<div style="OVERFLOW: hidden; height:220px;" id="scroll">\r\n	<div style="OVERFLOW: hidden" id="scroll1">\r\n		<li>aaaaaaaaaaaaaaaaaaa</li>\r\n		<li>aaaaaaaaaaaaaaaaaaa</li>\r\n		<li>aaaaaaaaaaaaaaaaaaa</li>\r\n		<li>aaaaaaaaaaaaaaaaaaa</li>\r\n		<li>aaaaaaaaaaaaaaaaaaa</li>\r\n		<li>aaaaaaaaaaaaaaaaaaa</li>\r\n		<li>aaaaaaaaaaaaaaaaaaa</li>\r\n		<li>aaaaaaaaaaaaaaaaaaa</li>\r\n		<li>aaaaaaaaaaaaaaaaaaa</li>\r\n		<li>aaaaaaaaaaaaaaaaaaa</li>\r\n		<li>aaaaaaaaaaaaaaaaaaa</li>\r\n		<li>aaaaaaaaaaaaaaaaaaa</li>\r\n		<li>aaaaaaaaaaaaaaaaaaa</li>\r\n		<li>aaaaaaaaaaaaaaaaaaa</li>\r\n		<li>aaaaaaaaaaaaaaaaaaa</li>\r\n	</div>\r\n	<div id="scroll2"></div>\r\n</div>\r\n<script type="text/javascript">\r\n	var speeds=30;\r\n	var scroll=document.getElementById(''scroll'');\r\n	var scroll1=document.getElementById(''scroll1'');\r\n	var scroll2=document.getElementById(''scroll2'');\r\n	scroll2.innerHTML=scroll1.innerHTML\r\n	function h_marquee1(){\r\n		if(scroll2.offsetHeight-scroll.scrollTop<=0)\r\n		scroll.scrollTop-=scroll1.offsetHeight\r\n		else{\r\n			scroll.scrollTop++\r\n		}\r\n	}\r\n	var MyMar1=setInterval(h_marquee1,speeds)\r\n	scroll.onmouseover=function(){\r\n		clearInterval(MyMar1)\r\n	}\r\n	scroll.onmouseout=function(){\r\n		MyMar1=setInterval(h_marquee1,speeds)\r\n	}\r\n</script>', '', '', '', '', '不间断滚动', '2014-11-21 23:19:53', '14-11-22 09:13:18');
INSERT INTO `db_mode` VALUES (282, 'h_goTop', 'P_other', 'high', 0, 0, '', '/*返回顶部*/\r\n.h_goTop{ display:none; position:fixed; top:300px; left:50%; margin-left:550px; width:80px; _position:absolute; _top:expression(eval(document.documentElement.scrollTop+300)); _left:expression(eval(document.documentElement.scrollLeft+1450)); _margin-left:0;}', '<!--返回顶部 start-->\r\n<div class="h_goTop">\r\n	<a href="javascript:;">点击回顶部</a>\r\n</div><!--h_goTop end-->\r\n<!--返回顶部 end-->\r\n<script>$(".h_goTop").click(function(){$(this).h_goTop();});</script>\r\n<div class="test" style=" height:1000px;"></div><!--此句仅供测试，删-->', '', '', '', '', '返回顶部', '2014-12-28 15:33:40', '14-11-22 09:13:10');
INSERT INTO `db_mode` VALUES (283, 'HFocus', 'P_focus', 'mode', 0, 0, '两种模式：默认为：mode:\\"fade\\" ，另一种为：mode:\\"slide\\"<br />\r\n此实例为模式：slide<br />\r\nps: slide模式.ttl必须top:*px; 而fade模式可以 bottom:0;但要加 _white-space:nowrap;\r\n<xmp>设置：外容器定宽 + left:50% + margin-left:-半宽 --》 作用：无论屏宽比外容器宽还是窄，容器始终居中\r\n\r\n设置：img用背景图片且bg:center  --》 作用：无论图片原尺寸比容器宽还是窄，图片始终在容器内上下左右居中\r\n\r\n直接用<img>图片和用背景图片对比：\r\n--如不确定高度，要用<img>提供高度；\r\n--如图片不设置width100%，要用背景图片才能始终居中；\r\n</xmp>', '/*幻灯片*/\r\n/*圆角*/.yj5-o .o{ border-radius:5px; -webkit-border-radius:5px; -moz-border-radius:5px; -khtml-border-radius:5px;}\r\n.HTest .HFocus{ height:350px; position:relative;}\r\n.HTest .HFocus .item{ float:left;}\r\n.HTest .HFocus .item a{ display:block; height:350px; background-position:center; position:relative;}\r\n.HTest .HFocus .item .ttl_{ position:absolute; bottom:0; left:0; width:100%; height:40px; line-height:40px; text-indent:15px; background-color:#fff9; filter:alpha(opacity=80); background:rgba(255,255,255,.8);}\r\n.HTest .HFocus .os{ position:absolute; bottom:13px;}\r\n.HTest .HFocus .os .o{ display:inline; float:left; width:10px; height:10px; background-color:#999; cursor:pointer; text-indent:-9999px; _line-height:0;}\r\n.HTest .HFocus .os .o.on{ background-color:#3f86f0;}\r\n.HTest .HFocus .arrow{ position:absolute; width:30px; height:30px; background-color:#fff; cursor:pointer;}\r\n.HTest .HFocus .arrow.left{ left:10px;}\r\n.HTest .HFocus .arrow.right{ right:10px;}', '<!--幻灯片-->\r\n<div class="HFocus">\r\n	<div class="h_pics">\r\n		<div class="item"><a href="#" target="_blank" style="background-image:url(upload/pics/focus_1.jpg);"><p class="ttl_">幻灯片标题一</p></a></div><!---->\r\n		<div class="item"><a href="#" target="_blank" style="background-image:url(upload/pics/focus_2.jpg);"><p class="ttl_">幻灯片标题二</p></a></div><!---->\r\n		<div class="item"><a href="#" target="_blank" style="background-image:url(upload/pics/focus_2.jpg);"><p class="ttl_">幻灯片标题二</p></a></div><!---->\r\n		<div class="item"><a href="#" target="_blank" style="background-image:url(upload/pics/focus_2.jpg);"><p class="ttl_">幻灯片标题二</p></a></div><!---->\r\n	</div>\r\n	<div class="os yj5-o"></div>\r\n	<div class="arrow left"></div>\r\n	<div class="arrow right"></div>\r\n</div><!--[HFocus]-->\r\n<script type="text/javascript" src="js/self/h_pics.js"></script>\r\n<script>$(function(){$(".HTest .HFocus").H_pics({mode:"slide",pos_os:"center",gap_o:10,line:1,row:1,effect:"return",autoPlay:true,delayTime:4000});});</script>', '', '', '', '', '横向全屏', '2016-02-22 11:59:11', '15-01-26 02:52:13');
INSERT INTO `db_mode` VALUES (307, 'h_showTime', 'P_other', 'high', 0, 0, '', '', '<!--团购倒计时 start-->\r\n<p>团购一剩余时间：<span id="time_1"></span></p> \r\n<p>团购二剩余时间：<span id="time_2"></span></p> \r\n<script type="text/javascript" src="plus/self/h_showTime.js"></script>\r\n<script type="text/javascript"> \r\n	var st1 = new h_showTime(1,5); \r\n	st1.setTimeShow();\r\n	 \r\n	var st2 = new h_showTime(2, 10); \r\n	st2.setTimeShow(); \r\n</script> \r\n<!--团购倒计时 start-->', '', '', '', '', '多个团购倒计时', '2014-11-21 23:10:38', '14-11-25 09:42:16');
INSERT INTO `db_mode` VALUES (308, 'h_ad', 'P_other', 'high', 0, 0, '', '#h_ad{ display:none; width:1000px; margin:0 auto; position:relative;}\r\n#h_ad img{ width:100%;}\r\n#h_ad .quit{ display:block; position:absolute; top:0; right:0; width:40px; height:20px; line-height:20px;}', '<!--顶部广告 start-->\r\n<div id="h_ad"><a href="#"><img src="upload/pics/focus_2.jpg" alt="#" /></a><a class="quit" href="javascript:;">关闭</a></div>\r\n<script type="text/javascript"> \r\n$(function(){\r\n	setTimeout(''$("#h_ad").slideDown(2000).delay(5000).slideUp(2000)'',2000);\r\n	$("#h_ad .quit").click(function(){\r\n		$("#h_ad").stop().slideUp(50);\r\n	});\r\n});\r\n</script> \r\n<!--顶部广告 start-->', '', '', '', '', '广告几秒后退出', '2014-11-21 23:07:38', '14-11-25 09:42:06');
INSERT INTO `db_mode` VALUES (284, 'h_addFav', 'P_other', 'high', 0, 0, '', '', '<a href="javascript:h_addFav();">点击收藏本站</a>\r\n<script>\r\n//收藏本站\r\nfunction h_addFav(){\r\n	if(document.all){\r\n		window.external.addFavorite(''http://localhost'',''siteName'');\r\n	}else if(document.sidebar){\r\n		window.sidebar.addPanel(''siteName'',''http://localhost'','''');\r\n	}else{\r\n		alert("你可以按Ctrl+D键收藏本站！");\r\n	}\r\n}\r\n</script>', '', '', '', '', '收藏本站', '2014-11-21 23:13:24', '14-11-22 09:12:49');
INSERT INTO `db_mode` VALUES (285, 'h_setHome', 'P_other', 'high', 0, 0, '', '', '<a onClick="h_setHome(window.location)" href="javascript:void(0)">点击设为主页</a>\r\n<script>\r\n//设为主页\r\nfunction h_setHome(url){\r\n	if (document.all) {\r\n		document.body.style.behavior=''url(#default#homepage)'';\r\n		   document.body.setHomePage(url);\r\n	}else{\r\n		alert("您好,您的浏览器不支持自动设置页面为首页功能,请您手动在浏览器里设置该页面为主页页!");\r\n	}\r\n}\r\n\r\n\r\n/*二：\r\nfunction h_setHome(obj){\r\n  try{\r\n	obj.style.behavior=''url(#default#homepage)'';\r\n	obj.setHomePage(''http://www.aaa.com.cn/'');\r\n  }catch(e){\r\n	if(window.netscape){\r\n	  try{\r\n		netscape.security.PrivilegeManager.enablePrivilege("UniversalXPConnect");\r\n	  }catch(e){\r\n		alert("抱歉，此操作被浏览器拒绝！\r\n\r\n请在浏览器地址栏输入“about:config”并回车然后将[signed.applets.codebase_principal_support]设置为''true'';\r\n\r\n或您可选择手动将本站设置为首页。");\r\n	  };\r\n	}else{\r\n	  alert("抱歉，您所使用的浏览器无法完成此操作。\r\n\r\n您需要手动将本站设置为首页。");\r\n	};\r\n  };\r\n};\r\n*/\r\n</script>', '', '', '', '', '设为主页', '2014-11-21 23:12:17', '14-11-22 09:12:39');
INSERT INTO `db_mode` VALUES (286, 'pSearch', 'P_search', 'mode', 0, 0, '', '/*搜索框*/\r\n.pSearch{ width:200px; height:30px;}\r\n.pSearch .keyword{ float:left; width:163px; height:24px; line-height:26px; padding:2px 3px; border-right:none;}\r\n.pSearch .submit{ float:right; width:30px; height:30px; cursor:pointer;}', '<!--搜索框-->\r\n<form class="pSearch"  id="h_search" name="form_search" action="#" method="get">\r\n	<input type="text" class="keyword" name="keyword" placeholder="..." />\r\n	<input type="submit" class="submit" value=">" />\r\n</form><!--[pSearch]-->', '', '', '', '', '', '2016-03-06 01:44:16', '14-11-25 11:31:51');
