INSERT INTO `db_mode` VALUES (288, 'h_dblclick', 'P_other', 'high', 0, 0, '', '', '<a href=\\"javascript:;\\" data-role=\\"http://www.baidu.com\\">双击打开连接为：http://www.baidu.com</a>\r\n<!--Msearch end-->\r\n<script>\r\n$(function(){\r\n$(\\"a\\").dblclick(function(){\r\n	var src=$(this).attr(\\"data-role\\");\r\n	if(src){\r\n		window.open(\\"\\"+src+\\"\\");	\r\n	}\r\n});\r\n});\r\n</script>', '', '', '', '', '双击打开链接', '2014-09-14 10:31:02', '14-11-21 05:40:40');
INSERT INTO `db_mode` VALUES (289, 'h_win', 'P_other', 'high', 0, 0, '', '/*弹出层*/\r\n#h_window{ display:none; position:fixed; top:0; left:0; width:100%; height:80%; padding:10% 0; overflow-y:scroll;}\r\n#h_window .HArt{ width:1000px; padding:20px; margin:0 auto 100px; background-color:#fff; position:relative; z-index:10000; height:2000px; line-height:200px;}\r\n#h_window .h_quit{ position:absolute; top:0; right:0; line-height:24px;}', '<div class="h_window">\r\n<input type="hidden" name="cid" value="8" />\r\n	<li id="1"><a href="#"><img src="upload/pics/list1.jpg" alt="" /></a></li>\r\n	<li id="2"><a href="#"><img src="upload/pics/list2.jpg" alt="" /></a></li>\r\n	<li id="3"><a href="#"><img src="upload/pics/list3.jpg" alt="" /></a></li>\r\n</div><!--h_window end-->\r\n\r\n<!--弹出层 start-->\r\n<div id="h_window">\r\n	<div class="HArt HArt_cat8">\r\n		<h1 class="title"></h1>\r\n		<div class="content">\r\n			<p>弹出框显示正文</p>\r\n			<p>弹出框显示正文</p>\r\n			<p>弹出框显示正文</p>\r\n			<p>弹出框显示正文</p>\r\n			<p>弹出框显示正文</p>\r\n			<p>弹出框显示正文</p>\r\n			<p>弹出框显示正文</p>\r\n		</div>\r\n	<a class="h_quit" href="javascript:;">关闭</a>\r\n	</div><!--HArt end-->\r\n</div><!--#h_window end-->\r\n<!--弹出层 end-->\r\n<script>\r\n	$(function(){\r\n		$(".h_window a").click(function(){\r\n		\r\n		var pid=$(this).parents(".h_window").find("input[name=''cid'']").val();\r\n		var aid=$(this).parents("li").attr("id");\r\n		var submitdata={\r\n			aid:aid,\r\n			pid:pid\r\n		};\r\n		$.post(''testfile/ajax_Mbody.php'',submitdata,\r\n			function(data){\r\n				$("#h_window .HArt_cat"+pid+" .title").text(data["title"]);\r\n				$("#h_window .HArt_cat"+pid+" .content").text(data["content"]);\r\n			}\r\n		,''json'');\r\n		\r\n			$("#h_window").show();\r\n			$("#h_maskB").show();\r\n			$("html,body").css("overflow","hidden");\r\n		});\r\n		$("#h_maskB").add($(".h_quit")).click(function(){\r\n			$("#h_window").hide();\r\n			$("#h_maskB").hide();\r\n			$("html,body").css("overflow","auto");\r\n		});\r\n	});//--ready end--\r\n</script>', '', '', '', '', '弹出窗口(四周居中)', '2014-11-21 22:51:38', '14-11-21 05:37:13');
INSERT INTO `db_mode` VALUES (290, 'h_ajax', 'P_other', 'high', 0, 0, '', '', '<a id=\\"test\\" data-role=\\"$.ajax测试成功\\" href=\\"javascript:;\\">点击测试_$.ajax</a>\r\n<script>\r\n$(function(){\r\n	$(\\"#test\\").click(function(){\r\n		$.ajax({\r\n			type:\\"post\\",\r\n			url:\\"testfile/ajaxtest.php\\",\r\n			data:{data:$(\\"#test\\").attr(\\"data-role\\"),act:\\"test\\"},\r\n			dataType:\\"json\\",\r\n			success:function(data){\r\n				alert(data);\r\n			}\r\n		});\r\n	});\r\n});\r\n</script>', '', '', '', '', '异步数据传输', '2014-09-14 10:30:31', '14-11-21 05:40:29');
INSERT INTO `db_mode` VALUES (291, 'h_ajax_post', 'P_other', 'high', 0, 0, '', '', '<a id=\\"test\\" data-role=\\"$.post测试成功\\" href=\\"javascript:;\\">点击测试_$.post</a>\r\n<script>\r\n$(function(){\r\n	$(\\"#test\\").click(function(){\r\n		var data=$(\\"#test\\").attr(\\"data-role\\");\r\n		var submitdata={\r\n			data:data,\r\n			act:\\"test\\",\r\n		}\r\n		/******************/\r\n		$.post(\\''testfile/ajaxtest.php\\'',submitdata,\r\n			function(data){\r\n				alert(data);\r\n				\r\n				/*\r\n				 var myjson=\\"\\";\r\n				eval(\\''myjson=\\''+data+\\'';\\'');\r\n				alert(\\"\\"+myjson+\\"\\");\r\n				*/\r\n				\r\n			},\\''json\\''\r\n		);\r\n		/******************/\r\n		\r\n	});\r\n});\r\n/*\r\najax:要返回来的数据不乱码，\r\n情况一：function(data){},\\''json\\'' \r\n情况二：function(data){ var myjson=\\"\\"; eval(\\''myjson=\\''+data+\\'';\\''); alert(\\"\\"+myjson+\\"\\");}\r\n*/\r\n</script>', '', '', '', '', '异步数据传输', '2014-09-14 10:30:09', '14-11-25 09:21:01');
