INSERT INTO `db_mode` VALUES (305, 'HArt', 'P_art', 'mode', 0, 0, '', '.HTest{ width:800px; margin:0 auto;}\r\n.HTest .HArt{ overflow:hidden; _zoom:1;}\r\n.HTest .HArt .lt{ width:200px;}\r\n.HTest .HArt .lt .imgs{ width:100%; overflow:hidden;}\r\n.HTest .HArt .lt .imgs img{ width:100%; height:200px;}\r\n.HTest .HArt .lt .list{ width:100%; overflow:hidden;}\r\n.HTest .HArt .lt .list ul{ height:80px;}\r\n.HTest .HArt .lt .list li{ float:left; margin-right:5px; cursor:pointer;}\r\n.HTest .HArt .lt .list li img{ width:76px; height:76px; border:#eee 2px solid;}\r\n.HTest .HArt .lt .list li.on img{ border:#CC0000 2px solid;}\r\n.HTest .HArt .rt{ width:580px;}\r\n.HTest .HArt .rt h1{ line-height:26px; font-size:16px;}\r\n.HTest .HArt .rt .w{ margin-bottom:20px; overflow:hidden; _zoom:1;}\r\n.HTest .HArt .rt .w h6{ float:left; width:60px; text-align:right; font-size:14px; margin-right:15px;}\r\n.HTest .HArt .rt .w ul{ float:left; width:300px; overflow:hidden; _zoom:1;}\r\n.HTest .HArt .rt .w ul li{ display:block; float:left; background-color:#CCC; margin:0 10px 5px 0; cursor:pointer; white-space:nowrap;}\r\n.HTest .HArt .rt .w ul li:hover,.HTest .HArt .rt ul li.on{ background-color:#CC0000; color:#fff;}\r\n.HTest .HArt .rt .num a{ float:left; display:block; padding:0 10px; background-color:#333; color:#fff;}\r\n.HTest .HArt .rt .num input{ float:left; width:20px; text-align:center;}\r\n.HTest .HArt .rt .num p{ float:left; padding-left:20px;}', '<div class="HArt">\r\n	<div class="lt">\r\n		<div class="imgs">\r\n			<ul>\r\n				<li><img src="upload/pics/cloths_1.jpg" alt="" /></li>\r\n				<li><img src="upload/pics/cloths_2.jpg" alt="" /></li>\r\n			</ul>\r\n		</div>\r\n		<div class="list">\r\n			<ul>\r\n				<li><img src="upload/pics/cloths_1.jpg" alt="" /></li>\r\n				<li><img src="upload/pics/cloths_2.jpg" alt="" /></li>\r\n			</ul>\r\n		</div>\r\n	</div><!--lt-->\r\n	<div class="rt">\r\n	<form>\r\n		<ol>\r\n			<h1 class="d1">正文标题正文标题正文标题正文标题正文标题正文标题正文标题正文标题正文标题正文标题正文标题正文标题</h1>\r\n			<p class="d2">商品描述商品描述商品描述商品描述商品描述商品描述</p>\r\n			<!--价格-->\r\n			<div class="w">\r\n				<h6>价格：</h6>\r\n				<ul>\r\n					<em>￥<span id="iPrice">100</span></em><s>￥200</s>\r\n				</ul>\r\n			</div>\r\n			<!--颜色-->\r\n			<div class="w">\r\n				<h6>颜色：</h6>\r\n				<ul id="iColor">\r\n					<li data-role="c1">黑色</li>\r\n					<li data-role="c2">白色</li>\r\n					<li data-role="c3">黑色</li>\r\n					<li data-role="c4">白色</li>\r\n					<li data-role="c5">黑色</li>\r\n					<li data-role="c6">白色</li>\r\n					<li data-role="c7">黑色</li>\r\n					<li data-role="c8">白色</li>\r\n					<li data-role="c9">黑色</li>\r\n					<li data-role="c10">白色</li>\r\n					<li data-role="c11">黑色</li>\r\n					<li data-role="c12">白色</li>\r\n				</ul>\r\n			</div>\r\n			<!--规格-->\r\n			<div class="w">\r\n				<h6>规格：</h6>\r\n				<ul id="iSize">\r\n					<li data-role="L">大码</li>\r\n					<li data-role="S">小码</li>\r\n				</ul>\r\n			</div>\r\n			<!--数量-->\r\n			<div class="w">\r\n				<h6>数量：</h6>\r\n				<ul class="num">\r\n					<a class="add" href="#">+</a>\r\n					<input id="iNum" type="text" value="1" disabled="disabled" />\r\n					<a class="sub" href="#">-</a>\r\n					<p>库存<span id="iStock">5</span>件</p>\r\n				</ul>\r\n			</div>\r\n			<!--提交值-->\r\n			<div class="vals" style="display:none;">\r\n				<input type="hidden" name="pid" value="123" /><!--最终值：产品ID-->\r\n				<input type="hidden" name="iColor" value="" /><!--最终值：颜色-->\r\n				<input type="hidden" name="iSize" value="" /><!--最终值：规格-->\r\n				<input type="hidden" name="iNum" value="" /><!--最终值：数量-->\r\n			</div>\r\n			<!---->\r\n			<div class="btns">\r\n				<input class="cart" type="submit" name="cart" value="放入购物车" />\r\n				<input class="buy" type="submit" name="buy" value="立即购买" />\r\n			</div>\r\n		</ol>\r\n	</form>\r\n	</div><!--rt-->\r\n</div><!--HArt end-->\r\n<script>\r\n$(document).ready(function(){\r\n\r\n	//商品大图切换\r\n	$(".HArt .lt .imgs li").hide();\r\n	$(".HArt .lt .imgs li:first-child").show();\r\n	$(".HArt .lt .list li:first-child").addClass("on");\r\n	$(".HArt .lt .list li").hover(function(){\r\n		var on=$(this).index();\r\n		$(this).addClass("on").siblings("li").removeClass("on");\r\n		$(".HArt .lt .imgs li").hide().eq(on).show();\r\n	});\r\n	\r\n	//点击选项\r\n	$(".HArt .w li").click(function(){\r\n		$(this).addClass("on").siblings("li").removeClass("on");\r\n		var on=$(this).attr("data-role");\r\n		var name=$(this).parents("ul").attr("id");\r\n		$(".HArt .vals").find("input[name=''"+name+"'']").val(on);\r\n	});\r\n	\r\n	//默认值\r\n	var num=$("#iNum").val();\r\n	$("input[name=''iNum'']").val(num);\r\n	\r\n	//库存\r\n	var sum=$("#iStock").text();\r\n	sum=stock=sum-1;\r\n	$("#iStock").text(stock);\r\n	\r\n	//减数\r\n	$(".HArt .num .sub").click(function(){\r\n		var num=$("#iNum").val();\r\n		if(num>0){\r\n			num=parseFloat(num)-1;\r\n			stock=parseFloat(stock)+1;\r\n			$("#iNum").val(num);\r\n			$("input[name=''iNum'']").val(num);\r\n			$("#iStock").text(stock);\r\n		};\r\n	});\r\n	\r\n	//加数\r\n	$(".HArt .num .add").click(function(){\r\n		var num=$("#iNum").val();\r\n		if(num<=sum){\r\n			num=parseFloat(num)+1;\r\n			stock=parseFloat(stock)-1;\r\n			$("#iNum").val(num);\r\n			$("input[name=''iNum'']").val(num);\r\n			$("#iStock").text(stock);\r\n		}\r\n	});\r\n	\r\n	//加入购物车\r\n	$(".HArt .btns input").click(function(){\r\n		var pid=$("input[name=''pid'']").val();			\r\n		var color=$("input[name=''iColor'']").val();			\r\n		var size=$("input[name=''iSize'']").val();			\r\n		var num=$("input[name=''iNum'']").val();	\r\n		if(color==""){\r\n			alert("请选择颜色");\r\n			return false;\r\n		}else if(size==""){\r\n			alert("请选择规格");\r\n			return false;\r\n		}else if(num==0){\r\n			alert("数量不能为0");\r\n			return false;\r\n		}else{\r\n			var act=$(this).attr("name");\r\n			if(act=="cart"){//放入购物车\r\n				var submitdata={\r\n					pid:pid,\r\n					price:price,\r\n					color:color,\r\n					size:size,\r\n					num:num,\r\n					act:"cart"\r\n				};\r\n				$.post(''testfile/ajaxtest2.php'',submitdata,\r\n					function(data){\r\n						alert("已添加到购物车!");\r\n					}\r\n				,''json'');\r\n			}else{//立即购买\r\n				$(".HArt form").submit();\r\n			}\r\n		}		\r\n	});\r\n\r\n});//--ready end--\r\n</script>', '', '', '', '', '', '2014-12-27 12:16:43', '14-11-21 05:34:30');
