INSERT INTO `db_mode` VALUES (350, 'HCart', 'P_other', 'mode', 0, 0, '', '.add_sub{ height:30px; line-height:30px; margin:25px 0 0 10px;}\r\n	.add_sub a{ float:left; display:block; padding:0 10px; background-color:#333; color:#fff;}\r\n	.add_sub input{ float:left; width:20px; text-align:center;}\r\n.HTest{ width:1000px; margin:0 auto;}\r\n.HTest .HCart ul{ overflow:hidden; height:80px; line-height:80px; padding:10px 0; border-bottom:#ddd 1px solid;}\r\n.HTest .HCart ul li{ float:left; width:100px; text-align:center;}\r\n.HTest .HCart ul.hd{ height:30px; line-height:30px; font-weight:bold; background-color:#eee;}\r\n.HTest .HCart ul li.first{ width:450px; text-align:left; text-indent:10px;}\r\n.HTest .HCart ul li.check{ width:50px;}\r\n.HTest .HCart ul li.img img{ width:80px; height:80px;}\r\n.HTest .HCart ul li.des{ width:150px; line-height:22px;}\r\n.HTest .HCart ul li.attr{ width:150px; line-height:22px;}\r\n.HTest .HCart .unit{ border:#ddd 1px solid; margin-bottom:10px;}\r\n.HTest .HCart .unit_{ text-align:right; background-color:#eee;}\r\n.HTest .HCart .btm div{ float:left; overflow:hidden;}\r\n.HTest .HCart .btm div p{ float:left; padding:0 10px;}', '<div class="HCart">\r\n\r\n	<div class="unit">\r\n		<ul class="hd">\r\n			<li class="first">店铺：aaa店</li>\r\n			<li>单价</li>\r\n			<li>数量</li>\r\n			<li>小计</li>\r\n			<li>操作</li>\r\n		</ul><!--hd-->\r\n		<ul class="c_check">\r\n			<li class="check"><input type="checkbox" checked="checked" /></li>\r\n			<li class="img"><a href="#"><img src="upload/pics/cloths_1.jpg" alt="" /></a></li>\r\n			<li class="des"><a href="#">产品标题产品标题产品标题产品标题产品标题产品标题</a></li>\r\n			<li class="attr">颜色分类：红色<br />尺码：S</li>\r\n			<li class="price">￥<span class="cPrice">400</span></li>\r\n			<li class="num">\r\n				<div class="add_sub">\r\n					<a class="c_add" href="javascript:;">+</a>\r\n					<input class="cNum" type="text" value="1" disabled="disabled" />\r\n					<a class="c_sub" href="javascript:;">-</a>\r\n				</div><!--add_sub-->\r\n			</li>\r\n			<li class="sum">￥<span class="cSum">200</span></li>\r\n			<li class="c_del"><a href="javascript:;">删除</a></li>\r\n		</ul><!---->\r\n		<ul class="c_check">\r\n			<li class="check"><input type="checkbox" checked="checked" /></li>\r\n			<li class="img"><a href="#"><img src="upload/pics/cloths_1.jpg" alt="" /></a></li>\r\n			<li class="des"><a href="#">产品标题产品标题产品标题产品标题产品标题产品标题</a></li>\r\n			<li class="attr">颜色分类：红色<br />尺码：S</li>\r\n			<li class="price">￥<span class="cPrice">400</span></li>\r\n			<li class="num">\r\n				<div class="add_sub">\r\n					<a class="c_add" href="javascript:;">+</a>\r\n					<input class="cNum" type="text" value="1" disabled="disabled" />\r\n					<a class="c_sub" href="javascript:;">-</a>\r\n				</div><!--add_sub-->\r\n			</li>\r\n			<li class="sum">￥<span class="cSum">200</span></li>\r\n			<li class="c_del"><a href="javascript:;">删除</a></li>\r\n		</ul><!---->\r\n		<div class="unit_">商品总价：<em>￥<span class="cSums">400</span></em></div>\r\n	</div><!--unit end-->\r\n	\r\n	<div class="unit">\r\n		<ul class="hd">\r\n			<li class="first">店铺：aaa店</li>\r\n			<li>单价</li>\r\n			<li>数量</li>\r\n			<li>小计</li>\r\n			<li>操作</li>\r\n		</ul><!--hd-->\r\n		<ul class="c_check">\r\n			<li class="check"><input type="checkbox" checked="checked" /></li>\r\n			<li class="img"><a href="#"><img src="upload/pics/cloths_1.jpg" alt="" /></a></li>\r\n			<li class="des"><a href="#">产品标题产品标题产品标题产品标题产品标题产品标题</a></li>\r\n			<li class="attr">颜色分类：红色<br />尺码：S</li>\r\n			<li class="price">￥<span class="cPrice">400</span></li>\r\n			<li class="num">\r\n				<div class="add_sub">\r\n					<a class="c_add" href="javascript:;">+</a>\r\n					<input class="cNum" type="text" value="1" disabled="disabled" />\r\n					<a class="c_sub" href="javascript:;">-</a>\r\n				</div><!--add_sub-->\r\n			</li>\r\n			<li class="sum">￥<span class="cSum">200</span></li>\r\n			<li class="c_del"><a href="javascript:;">删除</a></li>\r\n		</ul><!---->\r\n		<div class="unit_">商品总价：<em>￥<span class="cSums">400</span></em></div>\r\n	</div><!--unit end-->\r\n	\r\n	<div class="btm">\r\n		<ol class="lt_">\r\n			<div><input type="checkbox" class="c_checkAll" /><label for="c_checkAll">全选</label></div>\r\n			<div><p><a href="#">继续购物</a> | <a class="c_delAll" href="javascript:;">清空购物车</a></p></div>\r\n		</ol>\r\n		<ol class="rt_">\r\n			<div>\r\n				<p>共<em class="cNumAll">3</em>件商品</p>\r\n				<p>邮费：<em class="cPost">0.00</em></p>\r\n				<p>商品应付总额：<em class="cSumAll">1111.00</em></p>\r\n			</div>\r\n			<div><input type="submit" value="去结算" /></div>\r\n		</ol>\r\n	</div><!--btm end-->\r\n	\r\n</div><!--HCart end-->\r\n<script>\r\n//购物车验证\r\n$(function(){\r\n\r\n	//初始\r\n	$(".HCart input[type=''checkbox'']").each(function(){\r\n		var checked=$(this).attr("checked");\r\n		if(checked){\r\n			$(this).parent().addClass("on");\r\n			$(this).parents("ul").addClass("checked");\r\n		}\r\n	});\r\n	$(".HCart .cSum").each(function(){\r\n		var	price_=$(this).parents("ul").find(".cPrice").text();\r\n		var num_=$(this).parents("ul").find(".cNum").val();\r\n		sum_=parseFloat(price_)*parseFloat(num_);\r\n		$(this).text(sum_);\r\n	});\r\n\r\n	//总件数\r\n	function fun_numAll(){\r\n		var len=$(".HCart .checked").length;\r\n		p_=[];\r\n		for(var k=0;k<len;k++){\r\n			p=$(".HCart .checked").eq(k).find(".cNum").val();\r\n			p_.push(p);\r\n		}\r\n		num_all=eval(p_.join("+"));\r\n		$(".cNumAll").text(num_all);\r\n	}\r\n	fun_numAll();\r\n	\r\n	//总价格\r\n	function fun_sumAll(){\r\n		var len=$(".HCart .unit").length;\r\n		var sumsA=[];\r\n		for(var x=0;x<=len-1;x++){\r\n			//$(".HCart .unit").eq(x).addClass("unit_"+x+"");\r\n			var len_ul=$(".HCart .unit").eq(x).find(".checked").length;\r\n			var sumA=[];\r\n			for(var x2=0;x2<=len_ul-1;x2++){\r\n				var	price_=$(".HCart .unit").eq(x).find(".checked").eq(x2).find(".cPrice").text();\r\n				var num_=$(".HCart .unit").eq(x).find(".checked").eq(x2).find(".cNum").val();\r\n				sum_=parseFloat(price_)*parseFloat(num_);\r\n				sumA.push(sum_);\r\n			}\r\n			sum=eval(sumA.join("+"));\r\n			$(".HCart .unit").eq(x).find(".cSums").text(sum);\r\n			sumsA.push(sum);\r\n		}\r\n		sum2=eval(sumsA.join("+"));\r\n		$(".cSumAll").text(sum2);\r\n	}\r\n	fun_sumAll();\r\n	\r\n	\r\n	//更改数量\r\n	$(".cNum").change(function(){\r\n		fun_sumAll();\r\n		fun_numAll();\r\n	});\r\n	\r\n	//减数\r\n	$(".c_sub").click(function(){\r\n		var num=$(this).siblings(".cNum").val();\r\n		if(num>0){\r\n			num=parseFloat(num)-1;\r\n			$(this).siblings(".cNum").val(num);\r\n			var on=$(this).parents("ul").attr("class");\r\n			if(on.indexOf("checked")>0){\r\n				var sum=$(this).parents("ul").find(".cSum").text();\r\n				var price=$(this).parents("ul").find(".cPrice").text();\r\n				sum=parseFloat(sum)-parseFloat(price);\r\n				$(this).parents("ul").find(".cSum").text(sum);\r\n			}\r\n			fun_sumAll();\r\n			fun_numAll();\r\n		}else(num=0);\r\n	});\r\n	\r\n	//加数\r\n	$(".c_add").click(function(){\r\n		var num=$(this).siblings(".cNum").val();\r\n		if(num<5){//库存数\r\n			num=parseFloat(num)+1;\r\n			$(this).siblings(".cNum").val(num);\r\n			var checked=$(this).parents("ul").attr("class");\r\n			if(checked.indexOf("checked")>0){\r\n				var sum=$(this).parents("ul").find(".cSum").text();\r\n				var price=$(this).parents("ul").find(".cPrice").text();\r\n				sum=parseFloat(sum)+parseFloat(price);\r\n				$(this).parents("ul").find(".cSum").text(sum);\r\n			}\r\n			fun_sumAll();\r\n			fun_numAll();\r\n		}else(num=5);\r\n	});\r\n	\r\n	//单独勾选\r\n	$(".HCart .check input").click(function(){\r\n		var cls_check=$(this).parent().attr("class");\r\n		var price=$(this).parents("ul").find(".cPrice").text();\r\n		var num=$(this).parents("ul").find(".cNum").val();\r\n		var sum=$(this).parents("ul").find(".cSum").text();\r\n		if(cls_check.indexOf("on")<0){\r\n			$(this).parent().addClass("on");\r\n			$(this).parents("ul").addClass("checked");\r\n			$(this).attr("checked","checked");\r\n			fun_sumAll();\r\n			fun_numAll();\r\n			sum=parseFloat(sum)+eval(parseFloat(price)*parseFloat(num));\r\n			$(this).parents("ul").find(".cSums").text(sum);\r\n		}else{\r\n			$(this).parent().removeClass("on");\r\n			$(this).parents("ul").removeClass("checked");\r\n			$(this).removeAttr("checked");\r\n			fun_sumAll();\r\n			fun_numAll();\r\n			sum=parseFloat(sum)-eval(parseFloat(price)*parseFloat(num));\r\n			$(this).parents("ul").find(".cSums").text(sum);\r\n		};\r\n	});\r\n	\r\n	//垃圾桶\r\n	$(".c_del").click(function(){\r\n		var checked=$(this).parents("ul").attr("class");\r\n		if(confirm("确定要删掉此商品记录吗？")){\r\n			if(checked.indexOf("checked")!=-1){\r\n				var price=$(this).parents("ul").find(".cPrice").text();\r\n				var num=$(this).parents("ul").find(".cNum").val();\r\n				var sum=$(".cSum").text();\r\n				sum=parseFloat(sum)-eval(parseFloat(price)*parseFloat(num));\r\n				$(".cSums").text(sum);\r\n			}\r\n				$(this).parents("ul").remove();\r\n		}\r\n	});\r\n	\r\n	//全选\r\n	$(".c_checkAll").click(function(){\r\n		var onAll=$(this).attr("class");\r\n		if(onAll.indexOf("on")!=-1){\r\n			$(".HCart .check").removeClass("on").removeAttr("checked");\r\n			$(".HCart ul").removeClass("checked");\r\n			$(".cSums").text(0);\r\n			$(this).removeClass("on");\r\n		}else{\r\n			$(".HCart .check").each(function(){\r\n				var on=$(this).attr("class");\r\n				if(on.indexOf("on")<0){\r\n					$(this).addClass("on").attr("checked","checked");\r\n					$(this).parents("ul").addClass("checked");\r\n				}\r\n			});\r\n			fun_sumAll();\r\n			$(this).addClass("on");\r\n		}\r\n	});\r\n	\r\n	//清空\r\n	$(".c_delAll").click(function(){\r\n		if(confirm("确定要清空购物车吗？")){\r\n			$(".HCart").remove();\r\n			//$(".emptyCart").show();\r\n		}\r\n	});\r\n});\r\n</script>', '', '', '', '', '', '2014-12-25 23:22:08', NULL);
