INSERT INTO `db_mode` VALUES (514, 'HForm3', 'P_form', 'mode', 0, 0, '', '/*按键*/\r\n	.MBtn{ display:block; width:100%; height:34px; line-height:34px; font-size:16px; letter-spacing:1px; text-align:center; border-radius:3px; -webkit-border-radius:3px; -moz-border-radius:3px; -khtml-border-radius:3px;}\r\n	.MBtn_2{ display:block; width:100%; height:34px; line-height:34px; font-size:16px; letter-spacing:1px; text-align:center; border-radius:3px; -webkit-border-radius:3px; -moz-border-radius:3px; -khtml-border-radius:3px;}\r\n	.orange{ background-color:#fdbb34; color:#fff;}\r\n	.grey{ background-color:#ccc; color:#fff;}\r\n	/*自定义多选框*/\r\n	.HCheck input{ display:none;}\r\n	.HCheck label{ display:inline-block; height:30px; line-height:30px; padding-left:25px; cursor:pointer; background:url(upload/refer/about1.png) no-repeat 0 center;}\r\n	.HCheck.on label{ background:url(upload/refer/about1_.png) no-repeat 0 center;}\r\n	/*自定义单选框*/\r\n	.HRadio{ height:32px; line-height:32px; overflow:hidden;}\r\n	.HRadio dl{ float:left; text-align:center; width:33.3%; +width:33%;}\r\n	.HRadio dl input{ display:none;}\r\n	.HRadio dl label{ display:inline-block; height:30px; line-height:30px; padding-left:25px; cursor:pointer; background:url(style/imgs/icon/icon236_.png) no-repeat 0 center;}\r\n	.HRadio dl.on label{ background:url(style/imgs/icon/icon236.png) no-repeat 0 center;}	\r\n	/*自定义下拉框*/\r\n	.pSelect{ overflow:hidden;}\r\n	.hSelect{ position:relative; float:left; width:33.3%; +width:33%;_width:27.5%;}\r\n	.hSelect .optionOn{ position:absolute; top:0; left:2%; width:92%; padding:0 2%; height:24px; line-height:24px; border:#e5e5e5 1px solid; cursor:pointer; background:#fff url(style/imgs/icon/icon238_b.png) no-repeat 96% center; display:none9;}\r\n	.hSelect select{ float:left; width:100%; height:26px; opacity:0; opacity:19;}\r\n	.hSelect select option{ height:32px; line-height:32px;}\r\n\r\n.HForm .li{ height:30px; line-height:30px; margin:15px 0; overflow:hidden;}\r\n.HForm .li a{ co