INSERT INTO `db_mode` VALUES (524, 'mCat5', 'M_cat', 'mode', 0, 0, '', '.mCat5{ text-align:center; border-top:#ddd 1px solid; border-bottom:#ddd 1px solid;}\r\n.mCat5 a{ position:relative; width:33.3%; height:35px; line-height:35px; border-right:#ddd 1px solid;}\r\n.mCat5 a:last-child{ border-right:none;}\r\n.mCat5 a.on{ color:#CC0000;}\r\n.mCat5 a.on:after{ content:''''; position:absolute; top:-1px; left:-1px; width:100%; height:100%; border:#CC0000 1px solid;}\r\n.sort_up{ position:absolute; top:3px; left:50%; margin-left:20px; width:0; height:0; border:5px transparent dashed; border-bottom:8px #666 solid;}\r\n.sort_down{ position:absolute; bottom:3px; left:50%; margin-left:20px; width:0; height:0; border:5px transparent dashed; border-top:8px #666 solid;}\r\n.on .sort_up.cur{ border-bottom:#ff7802 8px solid;}\r\n.on .sort_down.cur{ border-top:8px #ff7802 solid;}', '<section class="mCat5 flex">\r\n    <a href="javascript:;" class="on">销量</a>\r\n    <a class="price" href="javascript:;">价格<i class="sort_up"></i><i class="sort_down"></i></a>\r\n    <a href="javascript:;">人气</a>\r\n</section><!--mCat5 end-->\r\n<script>\r\n$(function(){\r\n	//点击排序\r\n	$(".mCat5 a").click(function(){\r\n		$(this).addClass("on").siblings("a").removeClass("on");	\r\n		var cls=$(this).attr("class");\r\n		if(cls.indexOf("price")!=-1){//价格有升降序\r\n			if(!$(this).find(".sort_up").hasClass("cur") && !$(this).find(".sort_down").hasClass("cur")){\r\n				$(this).find(".sort_up").addClass("cur");	\r\n			}else{\r\n				$(this).find(".sort_up").toggleClass("cur");	\r\n				$(this).find(".sort_down").toggleClass("cur");	\r\n			}\r\n		}\r\n	});\r\n});\r\n</script>', '', '', '', '', '', '2016-02-03 00:22:32', '15-11-25 11:45:53');
